INSERT INTO port_commentaire(numero, refSituation, numProfesseur, commentaire, dateCommentaire) VALUES (2, 2, 2, 'Dans le fichier pkt, vous pouvez ajouter une zone de texte pour :\r\n- recenser exactement les différents éléments ajoutés dans l''infrastructure existante,\r\n- décrire les opérations de configuration réalisées pour chaque élément\r\nCe texte peut aussi figurer dans une notice externe au fichier pkt. Il faut se dire que le fichier pkt seul sans explication ne montre pas à lui seul ce qui a été réalisé.', '2012-05-04');
INSERT INTO port_commentaire(numero, refSituation, numProfesseur, commentaire, dateCommentaire) VALUES (3, 1, 2, 'Trop d''activités pour cette seule situation professionnelle !\r\nRecentrez sur celles réellement mises en oeuvre, càd celles sur lesquelles vous pouvez rédiger une reformulation.', '2012-05-04');
